create table login_records
(
    id      bigint auto_increment
        primary key,
    user_id bigint                              null,
    time    timestamp default CURRENT_TIMESTAMP not null on update CURRENT_TIMESTAMP
)
    charset = utf8;


create table problem
(
    problem_id           int auto_increment
        primary key,
    title                varchar(255)                        null,
    knowledge_point      varchar(255)                        null,
    comeout              varchar(255)                        null,
    problem_picture_name varchar(255)                        null,
    picture_answer       varchar(50)                         null,
    add_time             timestamp default CURRENT_TIMESTAMP null
)
    charset = utf8;

